////                Functions for entity client                ////
// client communication state
var entityClientCommState = {
    IDLE: 0,
    HANDSHAKE_1_SENT: 10,
    IN_COMM: 30                    // Session message
};
/*
helperOptions = {
    msgType,
    payload,
    myNonce,
    entityClientState
}
*/
/*
helperEventHandlers = {
    onWrite,
    onEnd,
    onConnection
}
*/
/*
ret = {
    success,
    myNonce,
    newSessionKeyVal            
}
*/
function secureClientHelper(helperOptions, helperEventHandlers, options, eventHandlers) {
    // send handshake1
    if (helperOptions == null) {
        var myNonce = generateHSNonce();
        console.log('chosen nonce: ' + myNonce.inspect());

        var handshake1 = {nonce: myNonce};
        var buf = serializeHandshake(handshake1);
        var enc = encryptSessionMessage(buf, options.sessionKey, options.sessionCryptoSpec);
        var keyIdBuf = new Buffer(common.SESSION_KEY_ID_SIZE);
        keyIdBuf.writeUIntBE(options.sessionKey.id, 0, common.SESSION_KEY_ID_SIZE);

        var msg = {
            msgType: msgType.SKEY_HANDSHAKE_1,
            payload: Buffer.concat([keyIdBuf, enc])
        };
        helperEventHandlers.onWrite(common.serializeIoTSP(msg));

        console.log('switching to HANDSHAKE_1_SENT');
        var entityClientState = entityClientCommState.HANDSHAKE_1_SENT;
        return {myNonce: myNonce};
    }
    else if (helperOptions.msgType == msgType.SKEY_HANDSHAKE_2) {
        console.log('received session key handshake2!');
        if (helperOptions.entityClientState != entityClientCommState.HANDSHAKE_1_SENT) {
            eventHandlers.onError('Comm init failed: wrong sequence of handshake, disconnecting...');
            helperEventHandlers.onEnd();
            return;
        }

        var buf = decryptSessionMessage(helperOptions.payload, options.sessionKey, options.sessionCryptoSpec);
        var ret = parseHandshake(buf);
        console.log('client ret: ' + ret);

        if (helperOptions.myNonce.equals(ret.replyNonce)) {
            console.log('server authenticated/authorized by solving nonce!');
        }
        else {
            eventHandlers.onError('Comm init failed: server NOT verified, nonce NOT matched, disconnecting...');
            helperEventHandlers.onEnd();
            return;
        }

        var theirNonce = ret.nonce;
        var handshake3 = {replyNonce: theirNonce};

        var clientECDH;
        if (options.sessionCryptoSpec.diffieHellman) {
            console.log('Using Diffie-Hellman algorithm: ' + options.sessionCryptoSpec.diffieHellman);
            clientECDH = crypto.createECDH(options.sessionCryptoSpec.diffieHellman);
            handshake3.dhParam = clientECDH.generateKeys();
        }

        buf = serializeHandshake(handshake3);
        var enc = encryptSessionMessage(buf, options.sessionKey, options.sessionCryptoSpec);

        var msg = {
            msgType: msgType.SKEY_HANDSHAKE_3,
            payload: enc
        };
        helperEventHandlers.onWrite(common.serializeIoTSP(msg));

        if (options.sessionCryptoSpec.diffieHellman) {
            var newSessionKeyVal = clientECDH.computeSecret(ret.dhParam);
            console.log('new session key: ');
            console.log(newSessionKeyVal);
            helperEventHandlers.onConnection(newSessionKeyVal);
            return;
        }
        else {
            helperEventHandlers.onConnection(null);
            return;
        }
    }        
    else if (helperOptions.msgType == msgType.SECURE_COMM_MSG) {
        console.log('received secure communication message!');
        if (helperOptions.entityClientState == entityClientCommState.IN_COMM) {
            var ret = helperOptions.iotSecureSocket.receive(helperOptions.payload);
            if (!ret.success) {
                eventHandlers.onError(ret.error);
                return;
            }
            eventHandlers.onData(ret.data);
            return;
        }
        else {
            options.onError('Error: it is not in IN_COMM state, disconnecting...');
            helperEventHandlers.onEnd();
            return;
        }
    }
    eventHandlers.onError('Error: unexpected message from server, disconnecting...');
    helperEventHandlers.onEnd();
    return;
}

function initializeSecureCommunicationOnTCP(options, eventHandlers) {
    if (options.sessionKey == null) {
        eventHandlers.onError('Comm init failed: No available key');
        return;
    }

    var entityClientState = entityClientCommState.IDLE;

    // per connection information
    console.log('chosen commSessionKey: ');
    console.log(options.sessionKey);
    console.log('options');
    console.log(options);
    var myNonce;
    var iotSecureSocket = null;

    var entityClientSocket;

    function onEnd() {
        if (entityClientSocket != null) {
            entityClientSocket.end();
            entityClientSocket = null;
        }
    }
    function onWrite(buf) {
        entityClientSocket.write(buf);
    }
    function onConnection(newSessionKeyVal) {           
        var entityClientSocketEventHandlers = {
            onWrite: onWrite,
            onEnd: onEnd
        };
        if (newSessionKeyVal != null) {     // only when DH is used   
            // TODO: Update macKeyVal as well  
            options.sessionKey.cipherKeyVal = newSessionKeyVal;
        }
        iotSecureSocket = new IoTSecureSocket(entityClientSocketEventHandlers, options.sessionKey, options.sessionCryptoSpec);
        eventHandlers.onConnection(iotSecureSocket);

        console.log('switching to IN_COMM');
        entityClientState = entityClientCommState.IN_COMM;
    }
    var helperEventHandlers = {
        onWrite: onWrite,
        onEnd: onEnd,
        onConnection: onConnection
    }

    entityClientSocket = net.connect({host:options.serverHost, port: options.serverPort},
        function() {
            var ret = secureClientHelper(null, helperEventHandlers, options, eventHandlers);
            myNonce = ret.myNonce;
            entityClientState = entityClientCommState.HANDSHAKE_1_SENT;

    });

    entityClientSocket.on('end', function() {
        if (entityClientState == entityClientCommState.IN_COMM) {
            eventHandlers.onClose();
            return;
        }
        eventHandlers.onError('Comm init failed: disconnected from server during communication initialization.' +
            options.serverHost + ':' + options.serverport);
        entityClientState = entityClientCommState.IDLE;
    });
    entityClientSocket.on('error', function(message) {
        if (entityClientState == entityClientCommState.IN_COMM) {
            eventHandlers.onError(message);
            return;
        }
        eventHandlers.onError('Comm init failed: Error in comm init - details: ' + message);
        entityClientSocket.end();
        return;
    });

    var expectingMoreData = false;
    var obj;

    entityClientSocket.on('data', function(data) {
        if (!expectingMoreData) {
            obj = common.parseIoTSP(data);
            if (obj.payload.length < obj.payloadLen) {
                expectingMoreData = true;
                console.log('more data will come. current: ' + obj.payload.length
                    + ' expected: ' + obj.payloadLen);
            }
        }
        else {
            obj.payload = Buffer.concat([obj.payload, data]);
            if (obj.payload.length ==  obj.payloadLen) {
                expectingMoreData = false;
            }
            else {
                console.log('more data will come. current: ' + obj.payload.length
                    + ' expected: ' + obj.payloadLen);
            }
        }
        if (expectingMoreData) {
            // do not process the packet yet
            return;
        }
        var helperOptions = {
            msgType: obj.msgType,
            payload: obj.payload,
            myNonce: myNonce,
            entityClientState: entityClientState,
            iotSecureSocket: iotSecureSocket
        };
        secureClientHelper(helperOptions, helperEventHandlers, options, eventHandlers);
    });
}

function initializeSecureCommunicationOnUDP(options, eventHandlers) {
    var udpClientSocket = dgram.createSocket('udp4');
    var entityClientState = entityClientCommState.IDLE;
    var myNonce;
    var iotSecureSocket = null;

    function onEnd() {
        udpClientSocket.close();
    }
    function onWrite(buf) {
        udpClientSocket.send(buf, 0, buf.length, options.serverPort, options.serverHost, (err) => {
            if (err) {
                console.error('udp client error: ' + err);
            }
            //socket.close();
        });
    }
    function onActiveEnd() {
        console.log('in onActiveEnd');
        var buf = common.serializeIoTSP({
            msgType: msgType.FIN_SECURE_COMM,
            payload: new Buffer(0)
        });
        try {
            udpClientSocket.send(buf, 0, buf.length, options.serverPort, options.serverHost, (err) => {
                udpClientSocket.close();
                udpClientSocket = null;
            });
        }
        catch(err) {
            console.error('udp active end err: ' + err);
        }
        //udpClientSocket.close();
    }
    function onConnection(newSessionKeyVal) {
        var entityClientSocketEventHandlers = {
            onWrite: onWrite,
            onEnd: onActiveEnd
        };
        if (newSessionKeyVal != null) {     // only when DH is used     
            // TODO: Update macKeyVal as well  
            options.sessionKey.cipherKeyVal = newSessionKeyVal;
        }
        iotSecureSocket = new IoTSecureSocket(entityClientSocketEventHandlers, options.sessionKey, options.sessionCryptoSpec);
        eventHandlers.onConnection(iotSecureSocket);

        console.log('switching to IN_COMM');
        entityClientState = entityClientCommState.IN_COMM;
    }
    var helperEventHandlers = {
        onEnd: onEnd,
        onWrite: onWrite,
        onConnection: onConnection
    };

    udpClientSocket.on('listening', () => {
        var address = udpClientSocket.address();
        console.log('client listening ' + address.address + ':' + address.port);
    });

    udpClientSocket.on('message', (data, rinfo) => {
        // if it's auth hello?

        console.log('some message received?');
        console.log('Received %d bytes from %s:%d\n',
            data.length, rinfo.address, rinfo.port);

        var obj = common.parseIoTSP(data);
        if (obj.payload.length != obj.payloadLen) {
            console.error('wrong payload length: ' + obj.payload.length + ' should be: ' + obj.payloadLen);
            return;
        }
        var helperOptions = {
            msgType: obj.msgType,
            payload: obj.payload,
            myNonce: myNonce,
            entityClientState: entityClientState,
            iotSecureSocket: iotSecureSocket
        };
        secureClientHelper(helperOptions, helperEventHandlers, options, eventHandlers);
/*
        var helperOptions = {
            msgType: obj.msgType,
            payload: obj.payload,
            myNonce: myNonce
        };
        var ret = sendSessionKeyReqHelper(helperOptions, helperEventHandlers,
            options, sessionKeyRespHandler, callbackParams);
        if (!ret.success) {
            console.error(ret.error);
            udpClientSocket.end();
            return;
        }
        myNonce = ret.myNonce;
        */
    });

    udpClientSocket.bind();

    var ret = secureClientHelper(null, helperEventHandlers, options, eventHandlers);
    myNonce = ret.myNonce;
    entityClientState = entityClientCommState.HANDSHAKE_1_SENT;
    /*
    entityClientState = entityClientCommState.HANDSHAKE_1_SENT;
    udpClientSocket.send(entityHello, 0, entityHello.length, options.authPort, options.authHost, (err) => {
        //socket.close();
    });
    */
}

/*
options = {
    serverHost,
    serverPort,
    sessionKey = {val, absValidity},
    sessionCryptoSpec,
    sessionProtocol
}
*/
/*
eventHandlers = {
    onClose,
    onError,
    onData,
    onConnection
}
*/
exports.initializeSecureCommunication = function(options, eventHandlers) {
    console.log('Session protocol: ' + options.sessionProtocol);
    if (options.sessionProtocol === 'TCP') {
        initializeSecureCommunicationOnTCP(options, eventHandlers);
    }
    else if (options.sessionProtocol === 'UDP') {
        initializeSecureCommunicationOnUDP(options, eventHandlers);
    }
    else {
        console.error('Unknown Session protocol!');
    }
};

///////////////////////////////////////////////////////////////////
////                Functions for entity server                ////

var entityServerCommState = {
    IDLE: 0,
    WAITING_SESSION_KEY: 20,
    HANDSHAKE_1_RECEIVED: 21,
    HANDSHAKE_2_SENT: 22,
    IN_COMM: 30                    // Session message
};
/*
helperOptions = {
    msgType,
    payload,
    myNonce,
    entityServerState,
    entityServerSessionKey,
    serverECDH,
    iotSecureSocket,
    socketID
}
*/
/*
helperEventHandlers = {
    onWrite,
    onEnd,
    onUpdate(update),
    onConnection
}
*/
/*
update = {
    myNonce,
    entityServerState,
    entityServerSessionKey,
    serverECDH
}
*/
function secureServerHelper(helperOptions, helperEventHandlers, options, eventHandlers) {
    function sendHandshake2(handshake1Payload, serverSocket, sessionKey) {
        if (helperOptions.entityServerState != entityServerCommState.HANDSHAKE_1_RECEIVED) {
            eventHandlers.onServerError('Error during comm init - in wrong state, expected: HANDSHAKE_1_RECEIVED, disconnecting...');
            helperEventHandlers.onEnd();
            return;
        }

        var enc = handshake1Payload.slice(common.SESSION_KEY_ID_SIZE);
        var update = {};
        update.entityServerSessionKey = sessionKey;

        var buf = decryptSessionMessage(enc, update.entityServerSessionKey, options.sessionCryptoSpec);

        var ret = parseHandshake(buf);
        var theirNonce = ret.nonce;

        console.log('server ret: ' + ret);

        update.myNonce = generateHSNonce();
        console.log('chosen nonce: ' + update.myNonce.inspect());
        var handshake2 = {nonce: update.myNonce, replyNonce: theirNonce};

        if (options.sessionCryptoSpec.diffieHellman) {
            console.log('Using Diffie-Hellman algorithm: ' + options.sessionCryptoSpec.diffieHellman);
            update.serverECDH = crypto.createECDH(options.sessionCryptoSpec.diffieHellman);
            handshake2.dhParam = update.serverECDH.generateKeys();
        }

        buf = serializeHandshake(handshake2);
        enc = encryptSessionMessage(buf, update.entityServerSessionKey, options.sessionCryptoSpec);
        var msg = {
            msgType: msgType.SKEY_HANDSHAKE_2,
            payload: enc
        };
        helperEventHandlers.onWrite(common.serializeIoTSP(msg));
        console.log('switching to HANDSHAKE_2_SENT');
        update.entityServerState = entityServerCommState.HANDSHAKE_2_SENT;
        helperEventHandlers.onUpdate(update);
    };
    if (helperOptions.msgType == msgType.SKEY_HANDSHAKE_1) {
        console.log('received session key handshake1');
        if (helperOptions.entityServerState != entityServerCommState.IDLE) {
            eventHandlers.onServerError('Error during comm init - in wrong state, expected: IDLE, disconnecting...');
            helperEventHandlers.onEnd();
            return;
        }
        console.log('switching to HANDSHAKE_1_RECEIVED state.');
        helperOptions.entityServerState = entityServerCommState.HANDSHAKE_1_RECEIVED;
        helperEventHandlers.onUpdate({entityServerState: helperOptions.entityServerState});
        eventHandlers.onClientRequest(helperOptions.payload, helperOptions.entityServerSocket, sendHandshake2);
    }
    else if (helperOptions.msgType == msgType.SKEY_HANDSHAKE_3) {
        console.log('received session key handshake3!');
        if (helperOptions.entityServerState != entityServerCommState.HANDSHAKE_2_SENT) {
            console.log('Error: wrong sequence of handshake, disconnecting...');
            helperEventHandlers.onUpdate({entityServerState: entityServerCommState.IDLE});
            helperEventHandlers.onEnd();
            return;
        }
        var buf = decryptSessionMessage(helperOptions.payload, helperOptions.entityServerSessionKey, options.sessionCryptoSpec);
        var ret = parseHandshake(buf);
        console.log(ret);

        if (helperOptions.myNonce.equals(ret.replyNonce)) {
            console.log('client authenticated/authorized by solving nonce!');
        }
        else {
            console.log('Error: client NOT verified, nonce NOT matched, disconnecting...');
            helperEventHandlers.onUpdate({entityServerState: entityServerCommState.IDLE});
            helperEventHandlers.onEnd();
            return;
        }

        if (options.sessionCryptoSpec.diffieHellman) {
            var newSessionKeyVal = helperOptions.serverECDH.computeSecret(ret.dhParam);
            console.log('new session key: ');
            console.log(newSessionKeyVal);
            helperEventHandlers.onConnection(newSessionKeyVal);
            return;
        }
        helperEventHandlers.onConnection();
    }
    else if (helperOptions.msgType == msgType.SECURE_COMM_MSG) {
        console.log('received secure communication!');
        if (helperOptions.entityServerState != entityServerCommState.IN_COMM) {
            console.log('Error: it is not in IN_COMM state, disconnecting...');
            helperEventHandlers.onUpdate({entityServerState: entityServerCommState.IDLE});
            helperEventHandlers.onEnd();
            return;
        }
        var ret = helperOptions.iotSecureSocket.receive(helperOptions.payload);
        if (!ret.success) {
            eventHandlers.onError(ret.error, helperOptions.socketID);
            return;
        }
        eventHandlers.onData(ret.data, helperOptions.socketID);
    }
    else if (helperOptions.msgType == msgType.FIN_SECURE_COMM && options.sessionProtocol === 'UDP') {
        console.log('received fin secure communication on UDP!');
        if (helperOptions.entityServerState != entityServerCommState.IN_COMM) {
            console.log('Error: it is not in IN_COMM state, disconnecting...');
            helperEventHandlers.onUpdate({entityServerState: entityServerCommState.IDLE});
            return;
        }
        console.log('switching to IDLE...');
        helperEventHandlers.onUpdate({entityServerState: entityServerCommState.IDLE});
    }
    else {
        console.error('received unknown type of message! ' + helperOptions.msgType);
    }
}

function initializeSecureServerOnTCP(options, eventHandlers) {
    var connectionCount = 0;
    var entityServer = net.createServer(function(entityServerSocket) {
        console.log('Unidentified client connected');
        var entityServerState = entityServerCommState.IDLE;
        var myNonce;
        var entityServerSessionKey;
        var serverECDH;
        var socketID = -1;

        function onEnd() {
            if (entityServerSocket != null) {
                entityServerSocket.end();
                entityServerSocket = null;    
            }
        }
        function onWrite(buf) {
            entityServerSocket.write(buf);
        }
        function onConnection(newSessionKeyVal) {
            if (newSessionKeyVal != null) {
                // TODO: Update macKeyVal as well using DH exchange
                entityServerSessionKey.cipherKeyVal = newSessionKeyVal;
            }
            console.log('switching to IN_COMM');
            entityServerState = entityServerCommState.IN_COMM;
            var entityServerSocketEventHandlers = {
                onEnd: onEnd,
                onWrite: onWrite
            }
            iotSecureSocket = new IoTSecureSocket(entityServerSocketEventHandlers, entityServerSessionKey,
                options.sessionCryptoSpec);
            socketID = connectionCount++;
            var socketInstance = {
                'id': socketID,
                'remoteAddress': entityServerSocket.remoteAddress + ':' + entityServerSocket.remotePort,
                'status': 'open'
            };
            eventHandlers.onConnection(socketInstance, iotSecureSocket);
        }
        function onUpdate(update) {
            if (update.myNonce != null) {
                myNonce = update.myNonce;
            }
            if (update.entityServerState != null) {
                entityServerState = update.entityServerState;
            }
            if (update.entityServerSessionKey != null) {
                entityServerSessionKey = update.entityServerSessionKey;
            }
            if (update.serverECDH != null) {
                serverECDH = update.serverECDH;
            }
        }
        var helperEventHandlers = {
            onWrite: onWrite,
            onEnd: onEnd,
            onConnection: onConnection,
            onUpdate: onUpdate
        }
        entityServerSocket.on('end', function() {
            console.log('Unidentified client disconnected');
        });

        entityServerSocket.on('end', function() {
            if (entityServerState == entityServerCommState.IN_COMM) {
                eventHandlers.onClose(socketID);
                return;
            }
            else {
                eventHandlers.onServerError('Closed during comm init from ' +
                    entityServerSocket.remoteAddress + ':' + entityServerSocket.remotePort);
                return;
            }
        });
        
        entityServerSocket.on('error', function(message) {
            if (entityServerState == entityServerCommState.IN_COMM) {
                eventHandlers.onError(message, socketID);
                return;
            }
            else {
                eventHandlers.onServerError('Error during comm init, details: ' + message);
                return;
            }
        });

        var obj;
        var expectingMoreData = false;
        var iotSecureSocket = null;
        entityServerSocket.on('data', function(data) {
            if (!expectingMoreData) {
                obj = common.parseIoTSP(data);
                if (obj.payload.length < obj.payloadLen) {
                    expectingMoreData = true;
                    console.log('more data will come. current: ' + obj.payload.length
                        + ' expected: ' + obj.payloadLen);
                }
            }
            else {
                obj.payload = Buffer.concat([obj.payload, data]);
                if (obj.payload.length ==  obj.payloadLen) {
                    expectingMoreData = false;
                }
                else {
                    console.log('more data will come. current: ' + obj.payload.length
                        + ' expected: ' + obj.payloadLen);
                }
            }

            if (expectingMoreData) {
                // do not process the packet yet
                return;
            }
            var helperOptions = {
                msgType: obj.msgType,
                payload: obj.payload,
                myNonce: myNonce,
                entityServerState: entityServerState,
                entityServerSessionKey: entityServerSessionKey,
                serverECDH: serverECDH,
                iotSecureSocket: iotSecureSocket,
                socketID: socketID
            };
            secureServerHelper(helperOptions, helperEventHandlers, options, eventHandlers);
        });
    });
    entityServer.on('error', function(message) {
        eventHandlers.onServerError(message);
    });
    entityServer.listen(options.serverPort, eventHandlers.onServerListening);
}

function initializeSecureServerOnUDP(options, eventHandlers) {
    var udpServerSocket = dgram.createSocket('udp4');
    var entityServerState = entityServerCommState.IDLE;
    var myNonce;
    var entityServerSessionKey;
    var serverECDH;
    var socketID = -1;
    var iotSecureSocket = null;
    udpServerSocket.on('error', (err) => {
        console.log('server error:\n' + err.stack);
        udpServerSocket.close();
    });

    udpServerSocket.on('message', (data, rinfo) => {
        function onEnd() {
            udpServerSocket.close();
        }
        function onWrite(buf) {
            udpServerSocket.send(buf, 0, buf.length, rinfo.port, rinfo.address, (err) => {
                if (err) {
                    console.error('udp server error: ' + err);
                }
                //socket.close();
            });
        }
        function onUpdate(update) {            
            if (update.myNonce != null) {
                myNonce = update.myNonce;
            }
            if (update.entityServerState != null) {
                entityServerState = update.entityServerState;
            }
            if (update.entityServerSessionKey != null) {
                entityServerSessionKey = update.entityServerSessionKey;
            }
            if (update.serverECDH != null) {
                serverECDH = update.serverECDH;
            }
        }
        function onConnection(newSessionKeyVal) {
            if (newSessionKeyVal != null) {
                // TODO: Update macKeyVal as well using DH exchange
                entityServerSessionKey.cipherKeyVal = newSessionKeyVal;
            }
            console.log('switching to IN_COMM');
            entityServerState = entityServerCommState.IN_COMM;
            var entityServerSocketEventHandlers = {
                onEnd: onEnd,
                onWrite: onWrite
            }
            iotSecureSocket = new IoTSecureSocket(entityServerSocketEventHandlers, entityServerSessionKey,
                options.sessionCryptoSpec);
            //socketID = connectionCount++;
            var address = udpServerSocket.address();
            var socketInstance = {
                'id': 0,
                'remoteAddress': address.address + ':' + address.port,
                'status': 'open'
            };
            eventHandlers.onConnection(socketInstance, iotSecureSocket);

        }
        var helperEventHandlers = {
            onEnd: onEnd,
            onWrite: onWrite,
            onUpdate: onUpdate,
            onConnection: onConnection
        };

        console.log('some message received?');
        console.log('Received %d bytes from %s:%d\n', data.length, rinfo.address, rinfo.port);

        var obj = common.parseIoTSP(data);
        if (obj.payload.length != obj.payloadLen) {
            console.error('wrong payload length: ' + obj.payload.length + ' should be: ' + obj.payloadLen);
            return;
        }
        var helperOptions = {
            msgType: obj.msgType,
            payload: obj.payload,
            myNonce: myNonce,
            entityServerState: entityServerState,
            entityServerSessionKey: entityServerSessionKey,
            serverECDH: serverECDH,
            iotSecureSocket: iotSecureSocket,
            socketID: socketID
        };
        secureServerHelper(helperOptions, helperEventHandlers, options, eventHandlers);
    });

    udpServerSocket.on('listening', () => {
        var address = udpServerSocket.address();
        console.log(address);
        console.log('server listening ' + address.address + ':' + address.port);
        eventHandlers.onServerListening();
    });

    udpServerSocket.bind(options.serverPort);
}

/*
options = {
    serverPort,
    sessionCryptoSpec,
    sessionProtocol
}
*/
/*
eventHandlers = {
    onServerError,      // for server
    onServerListening,
    onClientRequest,    // for client's communication initialization request

    onClose,            // for individual sockets
    onError,
    onData,
    onConnection
}
*/
exports.initializeSecureServer = function(options, eventHandlers) {
    console.log('Session protocol: ' + options.sessionProtocol);
    if (options.sessionProtocol === 'TCP') {
        initializeSecureServerOnTCP(options, eventHandlers);
    }
    else if (options.sessionProtocol === 'UDP') {
        initializeSecureServerOnUDP(options, eventHandlers);
    }
    else {
        console.error('Unknown Session protocol!');
    }
};
